import useFormValidation from "../../utils/useFormValidation";
import PopupWithForm from "../popupWithForm/PopupWithForm";

export default function EditProfilePopup({ onOpen, onClose }) {
  const { inputValue, errorMessage, isValid, isInputValid, handleChange } =
    useFormValidation();

  return (
    <PopupWithForm
      name="edit-profile"
      title="Редактировать профиль"
      textButton="Сохранить"
      onOpen={onOpen}
      onClose={onClose}
    >
      <input
        className="form__field form__field_input_name"
        id="name-pfofile"
        type="text"
        name="nameProfile"
        placeholder="Имя"
        minLength={2}
        maxLength={40}
        required
        isInputValid={isInputValid.nameProfile ? nameProfile : 'form__field_error'}
        value={inputValue.nameProfile ? inputValue.nameProfile : ''}
        onChange={handleChange}
      />
      <div className="form__error-container">
        <span id="name-pfofile-error">{errorMessage.nameProfile}</span>
      </div>
      <input
        className="form__field form__field_input_job"
        id="job-profile"
        type="text"
        name="jobProfile"
        placeholder="Деятельность"
        minLength={2}
        maxLength={200}
        required
        value={inputValue.jobProfile ? inputValue.jobProfile : ''}
        onChange={handleChange}
      />
      <div className="form__error-container">
        <span id="job-profile-error">{errorMessage.jobProfile}</span>
      </div>
    </PopupWithForm>
  );
}
