import PageLogin from "../pageLogin/PageLogin";
import useFormValidation from "../../utils/useFormValidation";

export default function Login({ name, handleLogin }) {
  console.log(name)
  const {
    inputValue,
    errorMessage,
    isValid,
    isInputValid,
    handleChange,
    reset,
    values
  } = useFormValidation();

  function onLogin(event) {
    event.preventDefault();
    handleLogin(values.password, values.email);
  }


  return (
    <PageLogin name={name} onSubmit={onLogin} isValid={isValid}>
      <input
        className={`form__field form__field_input_name form__field_input_title ${
          isInputValid.email === undefined || isInputValid.email
            ? ""
            : "form__error"
        } ${name === 'signIn' ? 'form__field_register' : ''}`}
        id="email-login"
        type="email"
        name="email"
        placeholder="Email"
        minLength={3}
        maxLength={30}
        required
        value={inputValue.email ? inputValue.email : ""}
        onChange={handleChange}
      />
      <div
        className={`form__error-container ${
          isInputValid.email ? "" : "form__field_error"
        }`}
      >
        <span id="name-img-error">{errorMessage.email}</span>
      </div>
      <input
        className={`form__field form__field_input_job form__field_input_url ${
          isInputValid.password === undefined || isInputValid.password
            ? ""
            : "form__error"
        }`}
        id="password-login"
        type="password"
        name="password"
        placeholder="Пароль"
        required
        value={inputValue.password ? inputValue.password : ""}
        onChange={handleChange}
      />
      <div
        className={`form__error-container ${
          isInputValid.password ? "" : "form__field_error"
        }`}
      >
        <span id="url-img-error">{errorMessage.password}</span>
      </div>
    </PageLogin>
  );
}
