export default function Form({
  name,
  textButton,
  children,
  onSubmit,
  isValid,
  title,
}) {

  return (
    <form name={name} noValidate onSubmit={onSubmit}>
      <h3
        className={`form__title ${name==='signIn' || name==='signUp' ? 'form__title_register' : '' }`}
      >
        {title}
      </h3>
      {children}
      {
        {
          login:
            <button
              className={`submit submit_register ${isValid ? "" : "submit_disabled"}`}
              type="submit"
              aria-label="login-form"
              disabled={isValid ? false : true}
            >
              {textButton}
            </button>,
          popup:
            <button
              className={`submit submit_register ${isValid ? "" : "submit_disabled"}`}
              type="submit"
              aria-label="register-form"
              disabled={isValid ? false : true}
            >
              {textButton}
            </button>
        }[`${name === "signIn" || name === "signUp" ? "login" : "popup"}`]
      }
      {/* <button
        className={`submit ${isValid ? "" : "submit_disabled"}`}
        type="submit"
        aria-label="save-form"
        disabled={isValid ? false : true}
      >
        {textButton}
      </button> */}
    </form>
  );
}
