import { useRef } from "react";
import useFormValidation from "../../utils/useFormValidation";
import PopupWithForm from "../popupWithForm/PopupWithForm";

export default function EditAvatarPopup({ onOpen, onClose, onUpdateAvatar }) {
  const input = useRef();
  const {
    inputValue,
    errorMessage,
    isValid,
    isInputValid,
    handleChange,
    reset,
  } = useFormValidation();

  function formReset() {
    onClose();
    reset();
  }

  function handleSubmit(event) {
    event.preventDefault();
    onUpdateAvatar({ avatar: input.current.value }, reset);
  }

  return (
    <PopupWithForm
      name="avatar-profle"
      title="Обновить аватар"
      textButton="Сохранить"
      onOpen={onOpen}
      onClose={formReset}
      isValid={isValid}
      onSubmit={handleSubmit}
    >
      <input
        ref={input}
        className={`form__field form__field_change_avatar ${
          isInputValid.avatar === undefined || isInputValid.avatar
            ? ""
            : "form__error"
        }`}
        id="url-avatar"
        type="url"
        name="avatarProfile"
        placeholder="Ссылка на аватар"
        required
        value={inputValue.avatar ? inputValue.avatar : ""}
        onChange={handleChange}
      />
      <div className="form__error-container">
        <span id="url-avatar-error">{errorMessage.avatar}</span>
      </div>
    </PopupWithForm>
  );
}
