import Form from "../form/Form";

export default function PageLogin({ title, name, isValid, onSubmit, children }) {

  return(
    <section className="login">
      <Form 
      title={title === 'signUp' ? 'Регистрация' : 'Вход'}
      name={name}
      textButton={name === 'signUp' ? 'Регистрация' : 'Войти'}
      children={children}
      isValid={isValid}
      onSubmit={onSubmit}
      />
      {name === 'signUp' && <p className="login__subtitle">Уже зарегестрированы? <Link to={'/sign-in'} className='login__subtitle-link'>Войти</Link></p>}
    </section>
  )
}
