export default function Form({
  name,
  textButton,
  children,
  onSubmit,
  isValid,
  title
}) {

  return (
    <form name={name} noValidate onSubmit={onSubmit}>
      <h3
        className={`form__title ${
          title === {name} //&& "form__title_delete"
        }`}
      >
        {title}
      </h3>
      {children}
      {{login:
       <button
       className={`submit__login ${isValid ? "" : "submit_disabled"}`}
       type="submit"
       aria-label="login-form"
       disabled={isValid ? false : true}
     >
       {textButton}
     </button>,
     popup:
     <button
     className={`submit ${isValid ? "" : "submit_disabled"}`}
     type="submit"
     aria-label="save-form"
     disabled={isValid ? false : true}
   >
     {textButton}
   </button>
      }[`${name === 'signIn' || name === 'signUp' ? 'login' : 'popup'}`]}
      {/* <button
        className={`submit ${isValid ? "" : "submit_disabled"}`}
        type="submit"
        aria-label="save-form"
        disabled={isValid ? false : true}
      >
        {textButton}
