import { Link } from "react-router-dom";
import Image from "../../images/logo.png";

export default function Header({ name, userEmail }) {
  function onSignOut() {
    localStorage.removeItem("jwt");
  }

  return (
    <header className="header">
      <img
        className="header__logo"
        src={Image}
        alt="Здесь должено быть лого страницы 'Места'"
      />
      {name === "signIn" || name === "signUp" ? (
        <Link
          to={name === "signUp" ? "/sign-in" : "/sign-up"}
          className="header__linkLogin"
        >
          {name === "signUp" ? "Войти" : "Регистрация"}
        </Link>
      ) : (
        <>
          <div>
            <p className="header__userEmail">{userEmail}</p>
            <Link
              to={"/sign-in"}
              className="header__logOut"
              onClick={onSignOut}
            >
              Выйти
            </Link>
          </div>
          {/* <button className="header__logOutBtn"></button> */}
        </>
      )}
    </header>
  );
}
